import React, { useState } from 'react';
import './Burgermenu.css'; // assuming your CSS file is named BurgerMenu.css

const BurgerMenu = () => {
  const [isOpen, setIsOpen] = useState(false);

  const handleClick = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div className={`burger-menu ${isOpen ? 'open' : ''}`} onClick={handleClick}>
      {isOpen ? (
        <svg className="close-icon" viewBox="0 0 20 20">
          <line x1="0" y1="0" x2="20" y2="20" stroke="#dfc385" strokeWidth="1.5" />
          <line x1="20" y1="0" x2="0" y2="20" stroke="#dfc385" strokeWidth="1.5" />
        </svg>
      ) : (
        <>
          <svg className="line" viewBox="0 0 20 1.5">
            <rect width="20" height="1.5" rx="0.5" fill="#dfc385" />
          </svg>
          <svg className="line" viewBox="0 0 20 1.5">
            <rect width="20" height="1.5" rx="0.5" fill="#dfc385" />
          </svg>
          <svg className="line" viewBox="0 0 20 1.5">
            <rect width="20" height="1.5" rx="0.5" fill="#dfc385" />
          </svg>
        </>
      )}
    </div>
  );
};

export default BurgerMenu;
