import React, { useState } from 'react';
import './Burgermenu.css'; // assuming your CSS file is named BurgerMenu.css

const BurgerMenu = () => {
  const [isOpen, setIsOpen] = useState(false);

  const handleClick = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div className={`burger-menu ${isOpen ? 'open' : ''}`} onClick={handleClick}>
      <svg className="line" viewBox="0 0 20 20">
        <line x1="1" y1="1" x2="19" y2="19" stroke="#dfc385" strokeWidth="1.5" />
      </svg>
      <svg className="line" viewBox="0 0 20 20">
        <line x1="1" y1="19" x2="19" y2="1" stroke="#dfc385" strokeWidth="1.5" />
      </svg>
    </div>
  );
};

export default BurgerMenu;
