// searchbarbutton.js

import React, { useState } from 'react';

const SearchBarButton = ({ onClick }) => {
  const [isSearching, setIsSearching] = useState(false);

  const handleClick = () => {
    setIsSearching(!isSearching);
    onClick(); // Execute the onClick function passed from the parent component
  };

  return (
    <svg
      xmlns="http://www.w3.org/2000/svg"
      width="24"
      height="24"
      viewBox="0 0 24 24"
      fill="none"
      stroke="#DFC385"
      strokeWidth="2"
      strokeLinecap="round"
      strokeLinejoin="round"
      className="search-button"
      onClick={handleClick}
      style={{ position: 'absolute', left: '20px', transition: 'transform 0.3s ease' }}
      transform={isSearching ? 'rotate(45)' : ''}
    >
      {isSearching ? (
        <>
          <line x1="5" y1="19" x2="23" y2="1" />
          <line x1="5" y1="5" x2="19" y2="19" />
        </>
      ) : (
        <>
          <circle cx="11" cy="11" r="8" />
          <line x1="21" y1="21" x2="16.65" y2="16.65" />
        </>
      )}
    </svg>
  );
};

export default SearchBarButton;
